mapper.update:
UPDATE USER_INFO
SET USER_STATUS = 'N'
, USER_MODIFY_DATE = SYSDATE
WHERE USER_ID = #{userId}
AND USER_PWD = #{userPwd}

UPDATE USER_INFO
SET USER_PWD = #{userPwd}
, USER_MODIFY_DATE = SYSDATE
WHERE USER_ID = #{userId}
AND USER_PWD = #{userPwd}

UPDATE USER_INFO
SET USER_ID = #{userId}
, USER_MODIFY_DATE = SYSDATE
WHERE USER_ID = #{userId}
AND USER_PWD = #{userPwd}

UPDATE USER_INFO
SET NICKNAME = #{nickName}
, USER_MODIFY_DATE = SYSDATE
WHERE USER_PWD = #{userPwd}
AND USER_ID = #{userId}
